USING NAMESPACE STD;

INT MAIN()
{
    CHAR STR1[50], STR2[50];
    COUT << "ENTER FIRST STRING: ";
    CIN >> STR1;
    COUT << "ENTER SECOND STRING: ";
    CIN >> STR2;

    INT M = STRLEN(STR1);
    INT N = STRLEN(STR2);

    INT ARR[M + 1][N + 1];
    INT MAXLEN = 0;
    INT ENDINDEX = M;

    FOR (INT I = 0; I <= M; I++)
    {
        FOR (INT J = 0; J <= N; J++)
        {
            IF (I == 0 || J == 0)
                ARR[I][J] = 0;
            ELSE IF (STR1[I - 1] == STR2[J - 1])
            {
                ARR[I][J] = ARR[I - 1][J - 1] + 1;
                IF (ARR[I][J] > MAXLEN)
                {
                    MAXLEN = ARR[I][J];
                    ENDINDEX = I;
                }
            }
            ELSE
                ARR[I][J] = 0;
        }
    }

    CHAR RES[MAXLEN + 1];
    RES[MAXLEN] = '\0';

    INT K = MAXLEN - 1;
    FOR (INT I = ENDINDEX - 1; I >= 0; I--)
    {
        IF (ARR[I + 1][N] == MAXLEN - K && ARR[I + 1][N] != 0)
        {
            RES[K] = STR1[I];
            K--;
        }
    }

    COUT << "THE LONGEST COMMON SUBSTRING OF GIVEN TWO STRINGS IS: " << RES;

    RETURN 0;
}

