FUNCTION GENERATEFIBONACCILIST(NUM) {
  IF (NUM <= 0 || ISNAN(NUM)) {
    RETURN "PLEASE ENTER A VALID POSITIVE INTEGER.";
  } ELSE {
    LET FIBONACCILIST = [0, 1];
    FOR (LET I = 2; I <= NUM; I++) {
