MODULE BINARY_ADDER(
  INPUT [7:0] A,
  INPUT [7:0] B,
  OUTPUT [8:0] SUM
);

  ASSIGN SUM[0] = 0; // INITIALIZE CARRY BIT TO 0

  FOR (INT I = 0; I < 8; I++) BEGIN
    ASSIGN SUM[I+1] = (A[I] ^ B[I]) ^ SUM[I]; // CALCULATE SUM BIT USING XOR GATE
    ASSIGN SUM[I+1] += (A[I] & B[I]) | (SUM[I] & (A[I] ^ B[I])); // CALCULATE CARRY BIT USING AND AND OR GATES
  END

ENDMODULE

